timeseries collection_time = sum(dt.runtime.jvm.gc.collection_time), by: { poolname, gcname }

26.query:
timeseries cpu_time = sum(dt.profiling.jvm.threads.cpu_time), by: { Thread_group, thread.group }
| sort arraySum(cpu_time) desc
| limit 20

35.query:
timeseries {
  reqCount=sum(dt.service.request.count)
  }, by: { dt.entity.service }
| fieldsAdd dt.entity.service.name = entityName(dt.entity.service)
| filterOut dt.entity.service.name == "Requests to unmonitored hosts"
| fieldsAdd value.B = arrayAvg(reqCount)
// | join [
//     timeseries ,
//     by: { dt.entity.service }
//     | fieldsAdd dt.entity.service.name = entityName(dt.entity.service)
//   ], on: { dt.entity.service }, fields: { `sum(dt.service.request.count)`, value.B }
| sort value.B DESC
| limit 15
| fieldsRemove value.B
